
#!/bin/bash
directory="/home/zhuzhu/rpmbuild-texlive-81/rpmbuild/SOURCES/"
file="/home/zhuzhu/rpmbuild-texlive-81/rpmbuild/list"

while read line; do
    echo ${line}
    mkdir ${directory}/${line} 2>/dev/null
    mv ${directory}/${line}.tar.xz ${directory}/${line}
    xz -dc ${directory}/${line}/${line}.tar.xz | tar x -C ${directory}/${line}
    rm ${directory}/${line}/${line}.tar.xz
done < ${file}

